import * as React from 'react';
import { useState } from 'react';
import { StyleSheet } from 'react-native';
import { PanGestureHandler } from 'react-native-gesture-handler';
import Animated from 'react-native-reanimated';
import SwipeBox from '../common/ActionBox';
import MainLayout from '../MainLayout';


import { Text, View } from '../Themed';


const runSpring = (clock, value, velocity, dest) => {
    const state = {
        finished: new Animated.Value(0),
        velocity: new Animated.Value(0),
        position: new Animated.Value(0),
        time: new Animated.Value(0)
    };

    const config = {
        damping: 7,
        mass: 1,
        stiffness: 121.6,
        overshootClamping: false,
        restSpeedThreshold: 0.001,
        restDisplacementThreshold: 0.001,
        toValue: new Animated.Value(0)
    };

    return [
        Animated.cond(Animated.clockRunning(clock), 0, [
            Animated.set(state.finished, 0),
            Animated.set(state.velocity, velocity),
            Animated.set(state.position, value),
            Animated.set(config.toValue, dest),
            Animated.startClock(clock)
        ]),
        Animated.spring(clock, state, config),
        Animated.cond(state.finished, Animated.stopClock(clock)),
        state.position
    ];
}

const TodayItem: React.FC = () => {
    const [translateX] = useState(new Animated.Value(0));

    //TODO This should not be running each time. Should I use 'useRef()'? 
    //or maybe just move this code ouside the functional component?
    const gestureEventHandler = Animated.event([
        {
            nativeEvent: {
                translationX: translateX
            }
        }
    ]);

    return (
        <PanGestureHandler onGestureEvent={gestureEventHandler}>
            <Animated.View
                style={[
                    styles.box,
                    {
                        transform: [{ translateX: translateX }]
                    }
                ]}>
                <TodayItemContent name={"Test"} isComplete={false} />
            </Animated.View>
        </PanGestureHandler>);
}

type TodayItemContentProps = {
    name: string;
    isComplete: boolean;
    startTime?: Date;
    endTime?: Date;
}
const TodayItemContent: React.FC<TodayItemContentProps> = (props) => {
    //TODO use the props to render the content properly
    return <View style={styles.box}><Text>{props.name}</Text></View>;
}

const styles = StyleSheet.create({
    box: {
        height: 100,
        alignSelf: 'stretch',
        backgroundColor: '#FDD7E4',
    }
});

export default TodayItem;
