import * as React from 'react';
import { useState } from 'react';
import { StyleSheet } from 'react-native';
import { PanGestureHandler } from 'react-native-gesture-handler';
import Animated from 'react-native-reanimated';
import SwipeBox from '../common/ActionBox';
import MainLayout from '../MainLayout';


import { Text, View } from '../Themed';


const TodayItem: React.FC = () => {
    const [translateX, setTranslateX] = useState(new Animated.Value(0));
    const gestureEventHandler = Animated.event([
        {
            nativeEvent: {
                translationX: translateX
            }
        }
    ]);

    return (
        <PanGestureHandler onGestureEvent={gestureEventHandler}>
            <Animated.View
                style={[
                    styles.box,
                    {
                        transform: [{ translateX: translateX }]
                    }
                ]}>
                <TodayItemContent name={"Test"} isComplete={false} />
            </Animated.View>
        </PanGestureHandler>);
}

type TodayItemContentProps = {
    name: string;
    isComplete: boolean;
    startTime?: Date;
    endTime?: Date;
}
const TodayItemContent: React.FC<TodayItemContentProps> = (props) => {
    //TODO use the props to render the content properly
    return <View style={styles.box}><Text>{props.name}</Text></View>;
}

const styles = StyleSheet.create({
    box: {
        height: 100,
        alignSelf: 'stretch',
        backgroundColor: '#FDD7E4',
    }
});

export default TodayItem;
