import * as React from 'react';
import { useState } from 'react';
import { StyleSheet } from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';
import Animated from 'react-native-reanimated';
import SwipeBox from '../common/ActionBox';
import MainLayout from '../MainLayout';

const {
    set,
    cond,
    eq,
    spring,
    startClock,
    stopClock,
    clockRunning,
    defined,
    Value,
    Clock,
    event
} = Animated;

import { Text, View } from '../Themed';


const runSpring = (clock: Animated.Clock, value: any, velocity: any, dest: any) => {
    const springState = {
        finished: new Value(0),
        velocity: new Value(0),
        position: new Value(0),
        time: new Value(0)
    };

    const config = {
        damping: 15,
        mass: 1,
        stiffness: 100,
        overshootClamping: false,
        restSpeedThreshold: 0.001,
        restDisplacementThreshold: 0.001,
        toValue: new Value(0)
    };

    return [
        cond(clockRunning(clock), 0, [
            set(springState.finished, 0),
            set(springState.velocity, velocity),
            set(springState.position, value),
            set(config.toValue, dest),
            startClock(clock)
        ]),
        spring(clock, springState, config),
        cond(springState.finished, stopClock(clock)),
        springState.position
    ];
}

const TodayItem: React.FC = (props) => {

    const dragDisplacementX = new Value(0);
    const gestureState = new Value(-1);
    const dragVelocityX = new Value(0);

    const clock = new Clock();
    const desiredTranslationX = new Value();

    //TODO This should not be running each time. Should I use 'useRef()'? 
    //or maybe just move this code ouside the functional component?
    const gestureEventHandler = event([
        {
            nativeEvent: {
                translationX: dragDisplacementX,
                velocityX: dragVelocityX,
                state: gestureState
            }
        }
    ]);

    const currTranslationX = cond(
        eq(gestureState, State.ACTIVE),
        [
            // When the guesture is active, display the dragged displacement
            stopClock(clock),
            set(desiredTranslationX, dragDisplacementX),
            desiredTranslationX
        ],
        [
            set(
                desiredTranslationX,
                cond(defined(desiredTranslationX), runSpring(clock, desiredTranslationX, dragVelocityX, 0), 0)
            )
        ]
    );



    return (
        <PanGestureHandler onGestureEvent={gestureEventHandler} onHandlerStateChange={gestureEventHandler}>
            <Animated.View
                style={[
                    styles.box,
                    {
                        transform: [{ translateX: currTranslationX }]
                    }
                ]}>
                <TodayItemContent name={"Test"} isComplete={false} />
            </Animated.View>
        </PanGestureHandler>);
}

type TodayItemContentProps = {
    name: string;
    isComplete: boolean;
    startTime?: Date;
    endTime?: Date;
}
const TodayItemContent: React.FC<TodayItemContentProps> = (props) => {
    //TODO use the props to render the content properly
    return <View style={styles.box}><Text>{props.name}</Text></View>;
}

const styles = StyleSheet.create({
    box: {
        height: 100,
        alignSelf: 'stretch',
        backgroundColor: '#FDD7E4',
    }
});

export default TodayItem;
