import * as React from 'react';
import { StyleSheet } from 'react-native';
import SwipeBox from '../components/common/ActionBox';
import MainLayout from '../components/MainLayout';


import { Text, View } from '../components/Themed';
import SwipeItem from '../components/today/TodayItem';

const TodayScreen: React.FC = () => {

    var content = [];
    for (let i = 0; i < 10; i++) {
        content.push()
    }
    const initialData = [...Array(NUM_ITEMS)].fill(0).map((d, index) => ({
        text: `Row ${index}`,
        key: `key-${index}`, // Note: It's bad practice to use index as your key. Don't do it in production!
        backgroundColor: getColor(index),

    }))
    return (
        <MainLayout title="Today" >
            <FlatList data={initialData}
                renderItem={() => <SwipeItem key={i}><TodayItemContent isComplete={false} name={"Item " + i} /></SwipeItem>}>

            </FlatList>
            {content}
        </MainLayout >
    );
}

const styles = StyleSheet.create({
    separator: {
        marginVertical: 30,
        height: 1,
        width: '80%',
    },
});


type TodayItemContentProps = {
    name: string;
    isComplete: boolean;
    startTime?: Date;
    endTime?: Date;
}
const TodayItemContent: React.FC<TodayItemContentProps> = (props) => {
    //TODO use the props to render the content properly
    return <Text>{props.name}</Text>;
}
const NUM_ITEMS = 10;
function getColor(i: number) {
    const multiplier = 255 / (NUM_ITEMS - 1)
    const colorVal = i * multiplier
    return `rgb(${colorVal}, ${Math.abs(128 - colorVal)}, ${255 - colorVal})`
}
export default TodayScreen;
